draw((0,2)--(1,2)--(1,1)--(2,1)--(2,3),linewidth(2));

draw((0,1)--(6,1));
draw((0,2)--(6,2));
draw((1,0)--(1,3));
draw((2,0)--(2,3));
draw((3,0)--(3,3));
draw((4,0)--(4,3));
draw((5,0)--(5,3));
[/asy]

[asy]
draw((0,0)--(6,0)--(6,3)--(0,3)--cycle,linewidth(2));
draw((0,1)--(2,1)--(2,2)--(1,2)--(1,3),linewidth(2));

draw((0,1)--(6,1));
draw((0,2)--(6,2));
draw((1,0)--(1,3));
draw((2,0)--(2,3));
draw((3,0)--(3,3));
draw((4,0)--(4,3));
